using Unity.AI.Assistant.Editor;

namespace Unity.AI.Assistant.UI.Editor.Scripts.Data
{
    class PluginButtonData
    {
        /// <summary>
        /// This is the info generated by the backend that dictates what plugin should be called with which parameters
        /// </summary>
        public PluginCallArguments CallArguments;

        /// <summary>
        /// This is the RegisteredPlugin information that exists on the local system. It contains information about how
        /// to render a plugin.
        /// </summary>
        public IAssistantProvider.RegisteredPlugin RegisteredPlugin;
    }
}
